SELECT object_id FROM authorization_rights WHERE object_type = #{dataObject.type} AND object_id IN
#{objectId}
GROUP BY object_id HAVING COUNT(group_id)=1

SELECT object_id FROM authorization_rights WHERE object_type = #{dataObject.type} AND object_id IN
#{objectId}
GROUP BY object_id HAVING COUNT(group_id)>1

SELECT MIN(group_id) FROM authorization_rights WHERE group_id IN
#{groupId}
GROUP BY object_type, object_id HAVING COUNT(group_id)=1

