mapper.select:
SELECT 

FROM user_info_group_relation ur
WHERE ur.relation_id = #{relationId,jdbcType=BIGINT}

mapper.delete:
DELETE FROM user_info_group_relation
WHERE relation_id IN

    #{relationId}

DELETE FROM user_info_group_relation
WHERE group_id IN

    #{groupId}

mapper.update:
UPDATE user_info_group_relation
SET user_id = #{userId,jdbcType=BIGINT},
  group_id = #{groupId,jdbcType=BIGINT}
WHERE relation_id = #{relationId,jdbcType=BIGINT}

mapper.select:
SELECT DISTINCT ur.user_id FROM user_info_group_relation ur, user_info ui WHERE ur.group_id IN
#{groupId}
AND ur.user_id = ui.user_id AND ui.is_delete = 1

SELECT DISTINCT ur.user_id FROM user_info_group_relation ur, user_info ui WHERE ur.user_id IN
#{userId}
AND ur.user_id = ui.user_id AND ui.is_delete = 1 GROUP BY ur.user_id HAVING COUNT(ur.group_id)=1

SELECT DISTINCT ur.user_id FROM user_info_group_relation ur, user_info ui WHERE ur.user_id IN
#{userId}
AND ur.user_id = ui.user_id AND ui.is_delete = 1 GROUP BY ur.user_id HAVING COUNT(ur.group_id)>1

SELECT DISTINCT ur.user_id FROM user_info_group_relation ur, user_info ui WHERE ur.user_id IN
#{userId}
AND ur.user_id = ui.user_id AND ui.is_delete = 1
